import { Injectable,EventEmitter } from "@angular/core";
import { ingredient } from "../MODELS/ingredient.model";
import { recipe } from "../MODELS/recipe.model";

@Injectable({
    providedIn:'root'
})

export class recipeListService{
    recipes:recipe[];
    selectedRecipe:EventEmitter<recipe>=new EventEmitter<recipe>();
    constructor(){
        this.recipes=[
            new recipe('1','Pizza','sadasdsdadsasasddsaads','data:image/jpeg;base64,',[new ingredient("Bread",4),new ingredient("tomato",10),new ingredient("cheese",4)]),
            new recipe('2','Noodles','saddsadasdasadssdasdasda','data:image/jpeg;base64,',[new ingredient("Carrot",4),new ingredient("noodle",10)])
          ];
    }

    getRecipes(){
        return this.recipes.slice();
    }

    getRecipeById(id:string){
        for(let recipe of this.getRecipes()){
            if(recipe.id === id){
                return recipe;
            }
        }

        return undefined;
    }
}